import React, {useEffect, useState} from "react";
import { Button, TextField, FormGroup } from "@material-ui/core";
import RaisedButton from "@material-ui/core/Button";
import CreateQ from '../styles/CreateQ.css';
import axios from "axios";

function CreateQuestion() {
 
  // const [worth, setWorth] = useState(0);
  // const [title, setTitle] = useState("");
  // const [trivia, setTrivia] = useState("");
   const [created, setCreated] = useState(true);
   
   useEffect(() => {
      // setCreated(false);
   }, [])

  function handleSubmit(event) {
    event.preventDefault();

    const worth = event.target.elements.worth.value;
    const title = event.target.elements.title.value;
    const trivia = event.target.elements.trivia.value;
    const hint = event.target.elements.hint.value;

    axios.post("http://localhost:8000/api/create-q/", {
        worth: worth,
        title: title,
        trivia: trivia,
        hint: hint
      })
      .then((response) => console.log(response))
      .catch((err) => console.log(err));

       alert(title + hint);

       setCreated(true);
  }

  return (
    <div className="question-creation-form">

      <main>
        <form onSubmitCapture={(event) => handleSubmit(event)}>
          <FormGroup>
            <TextField
              required
              id="filled-required"
              label="Question Worth"
              defaultValue="0"
              variant="filled"
              name="worth"
            />

            <TextField
              required
              id="filled-required"
              label="Title/question"
              defaultValue="?"
              variant="filled"
              name="title"
            />

            <TextField
              required
              id="filled-required"
              label="facts about answer..."
              defaultValue=""
              variant="filled"
              name="trivia"
            />

            <TextField
              required
              id="filled-required"
              label="Hint"
              defaultValue=""
              variant="filled"
              name="hint"
            />

            <Button
              variant="contained"
              color="primary"
              label="Submit"
              type="submit"
            >
              Submit
            </Button>
          </FormGroup>
        </form>
      </main>
    </div>
  );
}

export default CreateQuestion;
