import axios from "axios";
import "../styles/CreateQ.css";

function CreateOption() {

const [maxQID, setMaxQID] = useState(0); 

  useEffect(() => {

    fetch("http://localhost:8000/api/list-q/")
      .then((response) => response.json())
      .then((data) => {
        let arr = data, mx = 0;
        for(let i=0; i<arr.length; i++) {
          console.log(arr[i].id);
          mx = Math.max(mx, arr[i].id);
        }
        setMaxQID(mx);
        console.log(maxQID);
      });

  }, [maxQID])

const handleSubmit = (event) => {

    let choice = event.target.elements.choice1.value;

    axios.post("http://localhost:8000/api/create-c/", {
                question : maxQID + 1, 
                choice : choice,
                position : 1,
                is_correct : true,
      })
      .then((response) => console.log(response))
      .catch((err) => console.log(err));

    }

    return (
        <div className="question-creation-form">
            <form onSubmitCapture = {((event) => handleSubmit(event))}> 

                <TextField
                className = "fields"
                required
                id="filled-required"
                label="Choice"
                variant="filled"
                name="choice1"
                />
              
                <Checkbox
                className = "fields"
                // checked = "false"
                name="istrue1"
                />
    
            </form>
        </div>
    )
}

export default CreateOption
