import React, { useEffect, useState } from "react";
import {
  Button,
  TextField,
  TextareaAutosize,
  FormGroup,
  Checkbox,
  Grid,
} from "@material-ui/core";
// import Checkbox from '@mui/material/Checkbox';
import "../styles/CreateQ.css";
import axios from "axios";

function CreateQuestion() {

  const [maxQID, setMaxQID] = useState(0); 

  useEffect(() => {

    fetch("http://localhost:8000/api/list-q/")
      .then((response) => response.json())
      .then((data) => {
        setMaxQID(Math.max(maxQID, data.id));
        console.log(maxQID);
        console.log(data);
      });

  }, [])

  function handleSubmit(event) {
    // event.preventDefault();

    const worth = event.target.elements.worth.value;
    const title = event.target.elements.title.value;
    const trivia = event.target.elements.trivia.value;
    const hint = event.target.elements.hint.value;

    const istrue1 = event.target.elements.isCorrect1.value;
    const choice1 = event.target.elements.choice1.value;

    axios.all([
   
      axios.post("http://localhost:8000/api/create-q/", {
        worth: worth,
        title: title,
        trivia: trivia,
        hint: hint,
      }),
      axios.post("http://localhost:8000/api/create-c/", {
        question : title,
        choice : choice1,
        position : 1,
        is_correct : istrue1,
      })
    ])
    .then(axios.spread((data1, data2) => {
      // output of req.
      console.log('data1', data1, 'data2', data2)
    }));

    // axios
    //   .post("http://localhost:8000/api/create-q/", {
    //     worth: worth,
    //     title: title,
    //     trivia: trivia,
    //     hint: hint,
    //   })
    //   .then((response) => console.log(response))
    //   .catch((err) => console.log(err));

    alert("question created successfully");
  }

  return (
    <div className="question-creation-form">
      <main>
        <form onSubmitCapture={(event) => handleSubmit(event)}>
          <FormGroup style={{ margin: "10px" }}>

          <div>Worth (1 for ₹1000, 2 for ₹2000, 15 for ₹1 crore and so on) </div>

            <TextField
              className="fields"
              required
              id="filled-required"
              label="Worth ID"
              variant="filled"
              name="worth"
            />

            <div>Question</div>

            <TextField
              required
              id="filled-required"
              label="Title/question"
              defaultValue=""
              variant="filled"
              name="title"
              placeholder="Title"
            />

            <div> Option1 </div>
            
            <TextField
                className = "fields"
                required
                id="filled-required"
                label="Choice"
                variant="filled"
                name="choice1"
                />

                <Checkbox
                className = "fields"
                required
                name = "isCorrect1"
                />

           <div> Trivia </div>
            <TextField
              className="fields"
              required
              id="filled-required"
              label="Trivia"
              defaultValue=""
              variant="filled"
              name="trivia"
            />
            
            <div> Hint </div>
            <TextField
              required
              id="filled-required"
              label="Hint"
              defaultValue=""
              variant="filled"
              name="hint"
            />

            <Button
              variant="contained"
              color="primary"
              label="Submit"
              type="submit"
            >
              Submit
            </Button>
          </FormGroup>
        </form>
      </main>
    </div>
  );
}

export default CreateQuestion;
